import { useNavigate } from 'react-router-dom';
import { GameResult } from './GameResults';
import { FC, useEffect } from 'react';
import { NumberRolls } from './NumberRolls';

interface PlayProps {
  addNewGameResult: (result: GameResult) => void;
  addNewNumberRolls: (roll: NumberRolls) => void;
  setTitle: (t: string) => void;
}


export const Play: FC<PlayProps> = ({ addNewGameResult, addNewNumberRolls, setTitle }) => {

  useEffect(
    () => setTitle("Play Trash Pandas")
    , []
  );

  const nav = useNavigate();

  return (
    <div
      className='flex flex-col gap-3'
    >
      <button
        className="btn btn-lg btn-primary"
        onClick={() => {
          addNewNumberRolls({
            player: "Melisa",
            rolls: 5
          });

        }}
      >
        click to update Rolls
      </button>

      <button
        className="btn btn-lg btn-primary"
        onClick={() => {
          addNewGameResult({
            winner: "Tom"
            , players: [
              "Tom"
              , "Taylor"
            ]
          });
          nav(-2)
        }}
      >
        Done
      </button>
      <p className='text-xs'>
        Play the game and tap-a-tap-a-tap
      </p>
    </div>
  );
};


