import { useNavigate } from 'react-router-dom';

export const Play = () => {

  const nav = useNavigate();

  return (
    <>
      <h3>
        Play Trash Pandas
      </h3>
      <p>
        Play the game and tap-a-tap-a-tap
      </p>
      <div className="card-bordered w-96 bg-base-100  shadow-xl ">
        <div className="card-body">
          <h2 className="card-title underline">Player 1</h2>
          <div className="  card-actions justify-center">
            <p>Roll Count</p>
            <button className="btn btn-warning self-center p-3">-</button>
            <p className="p-3  border-b-2">#</p>
            <button className="btn btn-success p-3">+</button>
            {/* the p above is placeholder where I Will display # of rolls. also need to fix so all on one line  */}
          </div>
        </div>

      </div >

      <button
        className="btn btn-outline btn-primary"
        onClick={() => nav(-2)}
      >
        Done
      </button>
    </>
  );
};
